
[CmdletBinding(SupportsShouldProcess)]
param ()  

#Requires -Version 5
Set-StrictMode -Version 'Latest'

# ------------------------------------
# Script file - Assemblies - 
# ------------------------------------
$ScriptBlockAssemblies = ""

$ScriptBlockMusic = ""

# ------------------------------------
# Script file - Helpers - 
# ------------------------------------
$ScriptBlockHelpers = ""

# ------------------------------------
# Script file - MessageBox - 
# ------------------------------------
$ScriptBlockMessageBox = ""


function PopupMessage{
[CmdletBinding(SupportsShouldProcess)]
param ()  
    $Source = "$PWD\55.jpg"
    [int]$FontSize = 16
    [string]$Color='DimGray'
    $Image = New-Object System.Windows.Controls.Image
    $Image.Source = $Source
    $Image.Height = [System.Drawing.Image]::FromFile($Source).Height / 8
    $Image.Width = [System.Drawing.Image]::FromFile($Source).Width / 8
         
    Show-MessageBox -Content $Image -Title "Script Run From web!" -TitleFontWeight "Bold" -TitleBackground "$Color" -TitleTextForeground Black -TitleFontSize $FontSize -ContentBackground "$Color" -ContentFontSize ($FontSize-10) -ButtonTextForeground 'Black' -ContentTextForeground 'White'
}

# ------------------------------------
# Loader
# ------------------------------------
function ConvertFrom-Base64CompressedScriptBlock {

    [CmdletBinding()] param(
        [String]
        $ScriptBlock
    )

    # Take my B64 string and do a Base64 to Byte array conversion of compressed data
    $ScriptBlockCompressed = [System.Convert]::FromBase64String($ScriptBlock)

    # Then decompress script's data
    $InputStream = New-Object System.IO.MemoryStream(, $ScriptBlockCompressed)
    $GzipStream = New-Object System.IO.Compression.GzipStream $InputStream, ([System.IO.Compression.CompressionMode]::Decompress)
    $StreamReader = New-Object System.IO.StreamReader($GzipStream)
    $ScriptBlockDecompressed = $StreamReader.ReadToEnd()
    # And close the streams
    $GzipStream.Close()
    $InputStream.Close()

    $ScriptBlockDecompressed
}


# For each scripts in the module, decompress and load it.

$ScriptList = @('Assemblies','Music', 'Helpers', 'MessageBox')
$ScriptList | ForEach-Object {
    $ScriptBlock = "`$ScriptBlock$($_)" | Invoke-Expression
    ConvertFrom-Base64CompressedScriptBlock -ScriptBlock $ScriptBlock | Invoke-Expression
}

$Source = 'https://raw.githubusercontent.com/arsscriptum/PowerShell.Sandbox/main/Placeholder/img/55.jpg'
$CurrentDir=Get-ScriptDirectory
$rootPath = $CurrentDir | split-path

Register-Assemblies | Out-Null
NetGetFileNoCache $Source "$PWD\55.jpg"
InvokeMissionImpossible
PopupMessage